CREATE TABLE booking_entries (ID BIGINT NOT NULL, ACCEPTED SMALLINT DEFAULT 0 NOT NULL, CREATIONTIME BIGINT NOT NULL, login VARCHAR(64), booking_id BIGINT, PRIMARY KEY (ID));
CREATE TABLE bookings (ID BIGINT NOT NULL, DATE BIGINT NOT NULL, STATUS VARCHAR(255) NOT NULL, VERSION INTEGER, parking_uuid VARCHAR(36), PRIMARY KEY (ID));
CREATE UNIQUE INDEX INDEX_bookings_parking_uuid_date ON bookings (parking_uuid, date);
CREATE TABLE users (LOGIN VARCHAR(64) NOT NULL, ACTIVATIONDEADLINE BIGINT, ACTIVATIONUUID VARCHAR(36) UNIQUE, EMAIL VARCHAR(255) NOT NULL, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PASSWORD VARCHAR(64) NOT NULL, REMOVED SMALLINT DEFAULT 0 NOT NULL, VERSION INTEGER, PRIMARY KEY (LOGIN));
CREATE TABLE parkings (UUID VARCHAR(36) NOT NULL, CAPACITY INTEGER NOT NULL, NAME VARCHAR(255) NOT NULL, VERSION INTEGER, CITY VARCHAR(255), NUMBER VARCHAR(32), POSTALCODE VARCHAR(32), STREET VARCHAR(255), holiday_schedule_uuid VARCHAR(36), PRIMARY KEY (UUID));
CREATE TABLE records (ID BIGINT NOT NULL, POINTS INTEGER NOT NULL, VERSION INTEGER, VIP SMALLINT DEFAULT 0 NOT NULL, parking_uuid VARCHAR(36), login VARCHAR(64), PRIMARY KEY (ID));
CREATE TABLE holidays (ID BIGINT NOT NULL, DATE BIGINT NOT NULL, NOTE VARCHAR(255), REPEATEDEVERYYEAR SMALLINT DEFAULT 0 NOT NULL, holiday_schedule_uuid VARCHAR(36), PRIMARY KEY (ID));
CREATE TABLE holiday_schedules (UUID VARCHAR(36) NOT NULL, DAYOFWEEKMASK INTEGER, NAME VARCHAR(255) NOT NULL, VERSION INTEGER, PRIMARY KEY (UUID));
CREATE TABLE user_groups (login VARCHAR(64), group_name VARCHAR(255) NOT NULL);
ALTER TABLE booking_entries ADD CONSTRAINT booking_entries0 UNIQUE (login, booking_id);
ALTER TABLE records ADD CONSTRAINT UNQ_records_0 UNIQUE (login, parking_uuid);
ALTER TABLE user_groups ADD CONSTRAINT UNQ_user_groups_0 UNIQUE (login, group_name);
